# hyphenex.sh
#
# This small filter converts a hyphenation exception log article for
# TUGBoat to a real \hyphenation block.
#
# Written by Werner Lemberg <wl@gnu.org>.
#
# Version 1.0 (2003/01/25)

sed -n '
1 i\
% Hyphenation exceptions for US English, based on the hyphenation exception\
% log article in TUGBoat.\
%\
% This is an automatically generated file.  Do not edit!\
\
\\hyphenation{
$ i\
}\
\
% EOF
/^\\tabalign/ {
  s/{}//g
  s/[ 	][ 	]*/ /g
  s/ *\\[^ ][^ ]*$//
  s/\\tabalign *[^ ][^ ]*.*& *\(.*\)$/  \1/p
  b
}
/^\\[123456]/! d
s/\\4/\\1/
s/\\5/\\2/
s/\\6/\\3/
s/[ 	][ 	]*/ /g
s/^/  /
s/\\1 [^ ][^ ]* {\(.*\)}$/\1/
s/\\2 [^ ][^ ]* {\(.*\) \(.*\),\(.*\),\(.*\)}$/\1\n  \1\2\n  \1\3\n  \1\4/
s/\\2 [^ ][^ ]* {\(.*\) \(.*\),\(.*\)}$/\1\n  \1\2\n  \1\3/
s/\\2 [^ ][^ ]* {\(.*\) \(.*\)}$/\1\n  \1\2/
s/\\3 [^ ][^ ]* {\(.*\) \(.*\) \(.*\)}$/\1\2\n  \1\3/
s/\\1 [^ ][^ ]* {\(.*\)} .*$/\1/
s/\\2 [^ ][^ ]* {\(.*\) \(.*\),\(.*\),\(.*\)} .*$/\1\n  \1\2\n  \1\3\n  \1\4/
s/\\2 [^ ][^ ]* {\(.*\) \(.*\),\(.*\)} .*$/\1\n  \1\2\n  \1\3/
s/\\2 [^ ][^ ]* {\(.*\) \(.*\)} .*$/\1\n  \1\2/
s/\\3 [^ ][^ ]* {\(.*\) \(.*\) \(.*\)} .*$/\1\2\n  \1\3/
s/\\1 {.*} {\(.*\)}$/\1/
s/\\2 {.*} {\(.*\) \(.*\),\(.*\),\(.*\)}$/\1\n  \1\2\n  \1\3\n  \1\4/
s/\\2 {.*} {\(.*\) \(.*\),\(.*\)}$/\1\n  \1\2\n  \1\3/
s/\\2 {.*} {\(.*\) \(.*\)}$/\1\n  \1\2/
s/\\3 {.*} {\(.*\) \(.*\) \(.*\)}$/\1\2\n  \1\3/
s/\\1 {.*} {\(.*\)} .*$/\1/
s/\\2 {.*} {\(.*\) \(.*\),\(.*\),\(.*\)} .*$/\1\n  \1\2\n  \1\3\n  \1\4/
s/\\2 {.*} {\(.*\) \(.*\),\(.*\)} .*$/\1\n  \1\2\n  \1\3/
s/\\2 {.*} {\(.*\) \(.*\)} .*$/\1\n  \1\2/
s/\\3 {.*} {\(.*\) \(.*\) \(.*\)} .*$/\1\2\n  \1\3/
s/\\1 [^ ][^ ]* \([^ ][^ ]*\)$/\1/
s/\\2 [^ ][^ ]* \([^ ][^ ]*\) \([^ ][^ ]*\),\([^ ][^ ]*\),\([^ ][^ ]*\)$/\1\n  \1\2\n  \1\3\n  \1\4/
s/\\2 [^ ][^ ]* \([^ ][^ ]*\) \([^ ][^ ]*\),\([^ ][^ ]*\)$/\1\n  \1\2\n  \1\3/
s/\\2 [^ ][^ ]* \([^ ][^ ]*\) \([^ ][^ ]*\)$/\1\n  \1\2/
s/\\3 [^ ][^ ]* \([^ ][^ ]*\) \([^ ][^ ]*\) \([^ ][^ ]*\)$/\1\2\n  \1\3/
s/\\1 [^ ][^ ]* \([^ ][^ ]*\) .*$/\1/
s/\\2 [^ ][^ ]* \([^ ][^ ]*\) \([^ ][^ ]*\),\([^ ][^ ]*\),\([^ ][^ ]*\) .*$/\1\n  \1\2\n  \1\3\n  \1\4/
s/\\2 [^ ][^ ]* \([^ ][^ ]*\) \([^ ][^ ]*\),\([^ ][^ ]*\) .*$/\1\n  \1\2\n  \1\3/
s/\\2 [^ ][^ ]* \([^ ][^ ]*\) \([^ ][^ ]*\) .*$/\1\n  \1\2/
s/\\3 [^ ][^ ]* \([^ ][^ ]*\) \([^ ][^ ]*\) \([^ ][^ ]*\) .*$/\1\2\n  \1\3/
s/\*//
s/\\llap *{\(.*\)}/\1/
s/\\[^ ]* //
s/\\[^ ][^ ]*$//
s/{.*}//
p
'

# EOF
